mapper.select:
SELECT COUNT(0) FROM logistics_orders l
INNER JOIN express_goods g ON l.eg_id=g.eg_id
INNER JOIN item_type t ON g.it_id=t.it_id
INNER JOIN express_user u ON l.eu_id=u.eu_id
INNER JOIN transportation_status s ON l.ts_id=s.ts_id
where 1=1

    and t.it_id=#{id}


    and s.ep_id=${select01}


    AND s.ec_id=${select02}


    AND s.ea_id=${select03}


    and (u.eu_receipt_name like '%${name}%' or u.eu_sender_name like '%${name}%')
